create table decoration_manager(                                                                                                                                                                                                  
    id              int          AUTO_INCREMENT not null primary key comment '项目经理ID',                                                                                                                                            
    name            varchar(20)  not null                            comment '项目经理姓名',                                                                                                                                            
    avatar          varchar(256) not null                            comment '项目经理头像',
    birthday        date                                             comment '出生日期',                                                                                                                                                       
    hometown        int                                              comment '家乡城市编码',                                                                                                                                           
    workcity        int                                              comment '工作城市编码',                                                                                                                                           
    phone           varchar(20)                                      comment '手机号码',                                                                                                                                             
    company         int                                              comment '所属公司',                                                                                                                                             
    comments_number int          default 0                           comment '评价数量',                                                                                                                                             
    score           int          default 0                           comment '评价得分',                                                                                                                                             
    updtime         TIMESTAMP                                                                                                                                                                                                    
)ENGINE=MyISAM DEFAULT CHARSET=utf8;                                                                                                                                                                                             


